import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const CategoryDetail = () => {
  const { id } = useParams();
  const [category, setCategory] = useState({ name: '', items: [] });
  const [images, setImages] = useState([]); // State for images
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchCategory = async () => {
      try {
        // Fetch category details
        const categoryResponse = await axios.get(`/api/categories/${id}`);
        setCategory(categoryResponse.data);

        // Fetch images by category
        const imagesResponse = await axios.get(`/api/images/category/${id}`);
        setImages(imagesResponse.data);
      } catch (error) {
        console.error('Error fetching category details or images:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchCategory();
  }, [id]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if ((!category.items || category.items.length === 0) && images.length === 0) {
    return <div>No items or images found in this category.</div>;
  }

  return (
    <div>
      <h1>{category.name}</h1>
      
      <h2>Items</h2>
      <ul>
        {category.items.map((item) => (
          <li key={item.id}>{item.name}</li>
        ))}
      </ul>

      <h2>Images</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
        {images.map((image) => (
          <img 
            key={image.id} 
            src={image.url} 
            alt={image.name || 'Category image'} 
            style={{ width: '150px', height: '150px', objectFit: 'cover' }} 
          />
        ))}
      </div>
    </div>
  );
};

export default CategoryDetail;
