import React, { useState } from 'react';
import '../styles/ImageCard.css';

const ImageCard = ({ type, data, onClick, onFavorite, onTrash }) => {
  const [favorited, setFavorited] = useState(data.favorited || false);
  const [trashed, setTrashed] = useState(data.trashed || false);

  // Helper function to generate image URL
  const getImageUrl = (path) => {
    return path ? `http://localhost:5000/uploads/${path.split('\\').pop()}` : 'http://localhost:5000/uploads/default-image.jpg';
  };

  // Handle Favorite Button Click
  const handleFavoriteClick = (e) => {
    e.stopPropagation();  // Prevents triggering onClick for the image itself
    setFavorited(!favorited);
    if (onFavorite) onFavorite(data._id, !favorited); // Call the callback passed from parent to update the state
  };

  // Handle Trash Button Click
  const handleTrashClick = (e) => {
    e.stopPropagation();  // Prevents triggering onClick for the image itself
    setTrashed(true);
    if (onTrash) onTrash(data._id); // Call the callback to mark as trashed
  };

  // Prevent rendering if trashed
  if (trashed) return null;

  if (type === 'category') {
    const { name, images = [], count } = data;

    return (
      <div className="card category-card" onClick={onClick}>
        <div className="image-grid">
          {images.length > 0 ? (
            images.slice(0, 4).map((image) => {
              const imageUrl = getImageUrl(image.imagePath);
              return (
                <img
                  key={image._id}
                  src={imageUrl}
                  alt={name}
                  className="thumbnail"
                />
              );
            })
          ) : (
            <p>No images available</p>
          )}
        </div>
        <div className="category-info">
          <h3>{name}</h3>
          <p>{count} Images</p>
        </div>
      </div>
    );
  } else if (type === 'image') {
    const { name, tags = [], uploadedAt } = data;
    const formattedDate = uploadedAt ? new Date(uploadedAt).toLocaleDateString() : 'Unknown';
    const imageUrl = getImageUrl(data.imagePath);

    return (
      <div className="card image-card" onClick={() => onClick(data._id)}>
        <img
          src={imageUrl}
          alt={name}
          className="thumbnail"
        />
        <div className="image-info">
          <h4>{name}</h4>
          <p>Uploaded on: {formattedDate}</p>
          <p>Tags: {tags.join(', ')}</p>
        </div>
        <div className="card-actions">
          <button
            className={`favorite-btn ${favorited ? 'favorited' : ''}`}
            onClick={handleFavoriteClick}
          >
            {favorited ? '‚ù§Ô∏è' : 'üñ§'}
          </button>
          <button className="trash-btn" onClick={handleTrashClick}>
            üóëÔ∏è
          </button>
        </div>
      </div>
    );
  }
  return null;
};

export default ImageCard;
