import React, { useState } from 'react';
import '../styles/Sidebar.css';
import { FaFolder, FaPlus, FaHeart, FaTrash, FaUser } from 'react-icons/fa';

const Sidebar = ({ setView, onFilterChange }) => {
  const [isHovered, setIsHovered] = useState(false);

  const handleSwitchView = () => {
    setView((prevView) => (prevView === 'categories' ? 'images' : 'categories'));
  };

  const handleFavoritesClick = () => {
    onFilterChange('favorites');
  };

  const handleTrashClick = () => {
    onFilterChange('trash');
  };

  return (
    <div
      className={`sidebar ${isHovered ? 'expanded' : 'collapsed'}`}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Profile Section */}
      <div className="profile-section">
        <FaUser className="icon profile-icon" />
        {isHovered && <p className="username">John Doe</p>}
      </div>

      {/* Feature Icons */}
      <ul className="feature-list">
        <li onClick={handleSwitchView}>
          <FaFolder className="icon" />
          {isHovered && <span>Switch View</span>}
        </li>
        <li>
          <FaPlus className="icon" />
          {isHovered && <span>Add Image</span>}
        </li>
        <li onClick={handleFavoritesClick}>
          <FaHeart className="icon" />
          {isHovered && <span>Favorites</span>}
        </li>
        <li onClick={handleTrashClick}>
          <FaTrash className="icon" />
          {isHovered && <span>Trash</span>}
        </li>
      </ul>
    </div>
  );
};

export default Sidebar;
