import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import authService from '../services/authService'; // Import your authService

const SignInPage = ({ setIsAuthenticated }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSignIn = async (e) => {
    e.preventDefault();

    try {
      // Call authService for API authentication
      const response = await authService.signIn({ username, password });

      if (response.success) {
        // Store the user ID or token in localStorage
        localStorage.setItem('userId', response.userId);

        // Update authentication state
        setIsAuthenticated(true);

        // Redirect to the gallery page
        navigate('/gallery');
      } else {
        // Display error message from the API response
        setError(response.message || 'Failed to authenticate. Please try again.');
      }
    } catch (err) {
      // Handle unexpected errors
      setError('An error occurred. Please try again later.');
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h2>Sign In</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleSignIn}>
        <div>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div>
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Sign In</button>
      </form>
    </div>
  );
};

export default SignInPage;
