import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';  // Import axios for making API calls
import Navbar from '../components/NavbarGallery';
import Sidebar from '../components/SideBar';
import ImageCard from '../components/ImageCard';
import '../styles/Gallery.css';

const GalleryPage = () => {
  const [view, setView] = useState('categories');
  const [categories, setCategories] = useState([]);
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Fetch categories and images when component mounts or view changes
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        if (view === 'categories') {
          const categoryResponse = await axios.get('http://localhost:5000/api/categories');
          setCategories(categoryResponse.data);
        } else {
          const imageResponse = await axios.get('http://localhost:5000/api/images');
          setImages(imageResponse.data);
        }
      } catch (err) {
        setError('Failed to fetch data');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [view]);

  // Fetch filtered images for favorites or trash
  const handleFilterChange = async (filter) => {
    setLoading(true);
    try {
      let url = 'http://localhost:5000/api/images';
      if (filter === 'favorites') {
        url = 'http://localhost:5000/api/images/favorites'; // Add API route for favorites
      } else if (filter === 'trash') {
        url = 'http://localhost:5000/api/images/trash'; // Add API route for trash
      }

      const response = await axios.get(url);
      setImages(response.data); // Set images based on the filter
    } catch (err) {
      setError('Failed to fetch filtered data');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // Handle image click
  const handleCardClick = (type, id) => {
    if (type === 'category') {
      navigate(`/categories/${id}`);
    } else if (type === 'image') {
      navigate(`/images/${id}`);
    }
  };

  return (
    <div className="gallery-page">
      <Navbar />
      <div className="main-content">
        <Sidebar setView={setView} onFilterChange={handleFilterChange} />
        <div className="gallery-content">
          {loading && <p>Loading...</p>}
          {error && <p>{error}</p>}
          {view === 'categories'
            ? categories.map((category) => (
                <ImageCard
                  key={category._id}
                  type="category"
                  data={category}
                  onClick={() => handleCardClick('category', category._id)}
                />
              ))
            : images.map((image) => (
                <ImageCard
                  key={image._id}
                  type="image"
                  data={image}
                  onClick={() => handleCardClick('image', image._id)}
                />
              ))}
        </div>
      </div>
    </div>
  );
};

export default GalleryPage;
