import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Navbar from '../components/NavbarGallery';
import Sidebar from '../components/SideBar';
import ImageCard from '../components/ImageCard';
import '../styles/Gallery.css';

const GalleryPage = () => {
  const [images, setImages] = useState([]);
  const [filteredImages, setFilteredImages] = useState([]);
  const [view, setView] = useState('categories'); // Set initial view
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Fetch images when view is updated (for categories, favorites, or trash)
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        if (view === 'categories') {
          const categoryResponse = await axios.get('http://localhost:5000/api/categories');
          setImages(categoryResponse.data);
          elseif()
        } else if (view === 'favorites') {
          fetchFavoritedImages();  // Fetch only favorited images
        } else if (view === 'trash') {
          fetchTrashedImages();  // Fetch only trashed images
        }
      } catch (err) {
        setError('Failed to fetch data');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [view]);

  // Function to handle adding to favorites
  const handleAddToFavorites = async (id) => {
    try {
      await axios.put(`http://localhost:5000/api/images/${id}/favorite`, { favorited: true });
      setImages(images.map(image => image._id === id ? { ...image, favorited: true } : image));
    } catch (err) {
      console.error('Error adding to favorites:', err);
    }
  };

  // Function to handle adding to trash
  const handleAddToTrash = async (id) => {
    try {
      await axios.put(`http://localhost:5000/api/images/${id}/trash`, { trashed: true });
      setImages(images.map(image => image._id === id ? { ...image, trashed: true } : image));
    } catch (err) {
      console.error('Error adding to trash:', err);
    }
  };

  // Function to fetch favorited images
  const fetchFavoritedImages = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/images/favorites');
      setImages(response.data);
      setFilteredImages(response.data);  // Set filtered images to favorited ones
    } catch (err) {
      console.error('Error fetching favorited images:', err);
    }
  };

  // Function to fetch trashed images
  const fetchTrashedImages = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/images/trash');
      setImages(response.data);
      setFilteredImages(response.data);  // Set filtered images to trashed ones
    } catch (err) {
      console.error('Error fetching trashed images:', err);
    }
  };

  return (
    <div className="gallery-page">
      <Navbar />
      <div className="main-content">
        <Sidebar setView={setView} />  {/* Sidebar now updates the view */}
        <div className="gallery-content">
          {loading && <p>Loading...</p>}
          {error && <p>{error}</p>}
          {view === 'categories'
            ? images.map((category) => (
                <ImageCard
                  key={category._id}
                  type="category"
                  data={category}
                  onClick={() => navigate(`/categories/${category._id}`)}
                />
              ))
            : filteredImages.map((image) => (
                <ImageCard
                  key={image._id}
                  type="image"
                  data={image}
                  onClick={() => navigate(`/images/${image._id}`)}
                  onTrash={handleAddToTrash}  // Trash button handler
                  onFavorite={handleAddToFavorites}  // Favorite button handler
                />
              ))}
        </div>
      </div>
    </div>
  );
};

export default GalleryPage;
