import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Navbar from '../components/NavbarGallery';
import Sidebar from '../components/SideBar';
import ImageCard from '../components/ImageCard';
import '../styles/Gallery.css';

const GalleryPage = () => {
  const [view, setView] = useState('categories'); // 'categories' or 'images'
  const [categories, setCategories] = useState([]); // Store categories
  const [images, setImages] = useState([]); // Store images
  const [loading, setLoading] = useState(false); // Loading state
  const [error, setError] = useState(null); // Error state
  const navigate = useNavigate();

  // Fetch categories and images when component mounts or view changes
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        if (view === 'categories') {
          const categoryResponse = await axios.get('http://localhost:5000/api/categories');
          setCategories(categoryResponse.data);
        } else {
          const imageResponse = await axios.get('http://localhost:5000/api/images');
          setImages(imageResponse.data);
        }
      } catch (err) {
        setError('Failed to fetch data');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    
    fetchData();
  }, [view]); // Fetch data every time view changes

  const handleCardClick = (type, id) => {
    if (type === 'category') {
      navigate(`/category/${id}`); // Navigate to category page
    } else if (type === 'image') {
      navigate(`/image/${id}`); // Navigate to image details page
    }
  };

  // Handle favorite button click
  const handleFavorite = async (id, favoritedStatus) => {
    try {
      const response = await axios.put(`http://localhost:5000/api/images/${id}`, {
        favorited: favoritedStatus,
      });
      setImages((prevImages) =>
        prevImages.map((image) =>
          image.id === id ? { ...image, favorited: favoritedStatus } : image
        )
      );
    } catch (err) {
      console.error('Error updating favorite status:', err);
    }
  };

  // Handle trash button click
  const handleTrash = async (id) => {
    try {
      // You can update the trashed status or just hide the image in the UI
      await axios.put(`http://localhost:5000/api/images/${id}`, { trashed: true });
      
      // Hide the trashed image without deleting it
      setImages((prevImages) =>
        prevImages.map((image) =>
          image.id === id ? { ...image, trashed: true } : image
        )
      );
    } catch (err) {
      console.error('Error updating trashed status:', err);
    }
  };

  return (
    <div className="gallery-page">
      <Navbar />
      <div className="main-content">
        <Sidebar setView={setView} />
        <div className="gallery-content">
          {loading && <p>Loading...</p>}
          {error && <p>{error}</p>}
          {view === 'categories'
            ? categories.map((category) => (
                <ImageCard
                  key={category.id}
                  type="category"
                  data={category}
                  onClick={() => handleCardClick('category', category.id)}
                />
              ))
            : images.map((image) => (
                !image.trashed && (
                  <ImageCard
                    key={image.id}
                    type="image"
                    data={image}
                    onClick={() => handleCardClick('image', image.id)}
                    onFavorite={handleFavorite}
                    onTrash={handleTrash}
                  />
                )
              ))}
        </div>
      </div>
    </div>
  );
};

export default GalleryPage;
