import React, { useState } from 'react';
import '../styles/GalleryPage.css';

const GalleryPage = () => {
  const [isCategoryView, setIsCategoryView] = useState(true);

  // Mock data for categories and images
  const categories = [
    { id: 1, name: 'Nature', thumbnail: '/assets/nature.jpg', count: 25 },
    { id: 2, name: 'Travel', thumbnail: '/assets/travel.jpg', count: 18 },
    { id: 3, name: 'People', thumbnail: '/assets/people.jpg', count: 12 },
  ];

  const images = [
    { id: 1, title: 'Mountain View', src: '/assets/mountain.jpg', category: 'Nature' },
    { id: 2, title: 'Cityscape', src: '/assets/city.jpg', category: 'Travel' },
    { id: 3, title: 'Portrait', src: '/assets/portrait.jpg', category: 'People' },
  ];

  return (
    <div className="gallery-page">
      {/* Toggle Button for Category or Image View */}
      <div className="view-toggle">
        <button
          className={`toggle-btn ${isCategoryView ? 'active' : ''}`}
          onClick={() => setIsCategoryView(true)}
        >
          Categories
        </button>
        <button
          className={`toggle-btn ${!isCategoryView ? 'active' : ''}`}
          onClick={() => setIsCategoryView(false)}
        >
          Images
        </button>
      </div>

      {/* Render Categories or Images based on View */}
      <div className="content">
        {isCategoryView ? (
          // Category View
          <div className="category-grid">
            {categories.map((category) => (
              <div className="category-card" key={category.id}>
                <img src={category.thumbnail} alt={category.name} className="category-thumbnail" />
                <div className="category-info">
                  <h3>{category.name}</h3>
                  <p>{category.count} Images</p>
                </div>
              </div>
            ))}
          </div>
        ) : (
          // Image View
          <div className="image-grid">
            {images.map((image) => (
              <div className="image-card" key={image.id}>
                <img src={image.src} alt={image.title} className="image-thumbnail" />
                <div className="image-info">
                  <h4>{image.title}</h4>
                  <p>{image.category}</p>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default GalleryPage;
