import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const CategoryDetail = () => {
  const { id } = useParams();
  const [category, setCategory] = useState({ name: '', items: [] });
  const [images, setImages] = useState([]); // State for images
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null); // State for error handling

  useEffect(() => {
    const fetchCategory = async () => {
      try {
        // Fetch category details
        const categoryResponse = await axios.get(`http://localhost:5000/api/categories/${id}`);
        setCategory(categoryResponse.data);

        // Fetch images by category
        const imagesResponse = await axios.get(`http://localhost:5000/api/images/category/${id}`);
        setImages(imagesResponse.data);
      } catch (error) {
        console.error('Error fetching category details or images:', error);
        setError('Unable to fetch category details or images.');
      } finally {
        setLoading(false);
      }
    };

    fetchCategory();
  }, [id]);

  if (loading) {
    return <div>Loading, please wait...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  if ((!category.items || category.items.length === 0) && images.length === 0) {
    return <div>No items or images found in this category.</div>;
  }

  return (
    <div style={{ padding: '20px' }}>
      <h1>{category.name}</h1>

      {/* Display Items */}
      {category.items.length > 0 && (
        <>
          <h2>Items</h2>
          <ul>
            {category.items.map((item) => (
              <li key={item.id}>{item.name}</li>
            ))}
          </ul>
        </>
      )}

      {/* Display Images */}
      {images.length > 0 && (
        <>
          <h2>Images</h2>
          <div
            style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))',
              gap: '10px',
            }}
          >
            {images.map((image) => (
              <img
                key={image.id}
                src={image.url}
                alt={image.name || 'Category image'}
                style={{
                  width: '100%',
                  height: '150px',
                  objectFit: 'cover',
                  borderRadius: '8px',
                  border: '1px solid #ccc',
                }}
              />
            ))}
          </div>
        </>
      )}
    </div>
  );
};

export default CategoryDetail;
