import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const Sidebar = () => {
  const [view, setView] = useState('images'); // Default view is 'images'
  const [favorites, setFavorites] = useState([]);
  const [trash, setTrash] = useState([]);
  const [username, setUsername] = useState('');
  const userId = localStorage.getItem('userId'); // Retrieve user ID from localStorage
  const history = useHistory();

  useEffect(() => {
    if (userId) {
      fetchUsername(userId);
    }
  }, [userId]);

  useEffect(() => {
    if (view === 'favorites') {
      fetchFavorites();
    } else if (view === 'trash') {
      fetchTrash();
    }
  }, [view]);

  // Fetch the username based on userId
  const fetchUsername = async (userId) => {
    try {
      const response = await axios.get(`/api/users/${userId}`);
      setUsername(response.data.username);
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };

  // Fetch all favorited images
  const fetchFavorites = async () => {
    try {
      const response = await axios.get(`/api/images/favorites`);
      setFavorites(response.data);
    } catch (error) {
      console.error('Error fetching favorites:', error);
    }
  };

  // Fetch all images in trash
  const fetchTrash = async () => {
    try {
      const response = await axios.get(`/api/images/trash`);
      setTrash(response.data);
    } catch (error) {
      console.error('Error fetching trash:', error);
    }
  };

  // Handle the "Favorites" click
  const handleFavoritesClick = () => {
    setView('favorites');
  };

  // Handle the "Trash" click
  const handleTrashClick = () => {
    setView('trash');
  };

  // Undo the action for trash (remove from trash)
  const handleUndoTrash = async (imageId) => {
    try {
      await axios.put(`/api/images/${imageId}/undo-trash`);
      setTrash((prevTrash) => prevTrash.filter((image) => image._id !== imageId));
    } catch (error) {
      console.error('Error undoing trash:', error);
    }
  };

  // Handle view change for Images
  const handleImagesClick = () => {
    setView('images');
  };

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <h3>Welcome, {username || 'Guest'}</h3>
      </div>
      <div className="sidebar-buttons">
        <button onClick={handleImagesClick}>Images</button>
        <button onClick={handleFavoritesClick}>Favorites</button>
        <button onClick={handleTrashClick}>
          {view === 'trash' ? 'Undo' : 'Trash'}
        </button>
      </div>
      {view === 'favorites' && (
        <div className="favorites">
          <h4>Favorites</h4>
          <ul>
            {favorites.map((image) => (
              <li key={image._id}>
                <img src={image.imagePath} alt={image.description} width="50" />
                <p>{image.description}</p>
              </li>
            ))}
          </ul>
        </div>
      )}
      {view === 'trash' && (
        <div className="trash">
          <h4>Trash</h4>
          <ul>
            {trash.map((image) => (
              <li key={image._id}>
                <img src={image.imagePath} alt={image.description} width="50" />
                <p>{image.description}</p>
                <button onClick={() => handleUndoTrash(image._id)}>Undo</button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Sidebar;
