import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Navbar from '../components/NavbarGallery';
import Sidebar from '../components/SideBar';
import ImageCard from '../components/ImageCard';
import '../styles/Gallery.css';

const GalleryPage = () => {
  const [view, setView] = useState('categories');
  const [categories, setCategories] = useState([]);
  const [images, setImages] = useState([]);
  const [filteredImages, setFilteredImages] = useState([]); // New state for filtered images
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Fetch categories and images when component mounts or view changes
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        if (view === 'categories') {
          const categoryResponse = await axios.get('http://localhost:5000/api/categories');
          setCategories(categoryResponse.data);
        } else {
          const imageResponse = await axios.get('http://localhost:5000/api/images');
          setImages(imageResponse.data);
          // Set filtered images based on the selected view (favorited or trashed)
          if (view === 'favorites') {
            setFilteredImages(imageResponse.data.filter(image => image.favorited === true));
          } else if (view === 'trash') {
            setFilteredImages(imageResponse.data.filter(image => image.trashed === true));
          } else {
            setFilteredImages(imageResponse.data); // Reset to all images if 'categories'
          }
        }
      } catch (err) {
        setError('Failed to fetch data');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [view]);

  // Handle image click
  const handleCardClick = (type, id) => {
    if (type === 'category') {
      navigate(`/categories/${id}`);
    } else if (type === 'image') {
      navigate(`/images/${id}`);
    }
  };

  // Handle trash click - update trashed status in the database
  const handleTrash = async (id) => {
    try {
      await axios.put(`http://localhost:5000/api/images/${id}/trash`, { trashed: true });
      // Update the images state and filtered images state to reflect the trashed change
      setImages(images.filter(image => image._id !== id));
      setFilteredImages(filteredImages.filter(image => image._id !== id)); // Remove trashed image from filtered view
    } catch (err) {
      console.error('Error trashing image:', err);
    }
  };

  // Handle favorite click - update favorited status in the database
  const handleFavorite = async (id, favorited) => {
    try {
      await axios.put(`http://localhost:5000/api/images/${id}/favorite`, { favorited });
      // Optionally, update the local state to reflect changes
      setImages(images.map(image => 
        image._id === id ? { ...image, favorited } : image
      ));
      // Filter the images again after updating the favorited status
      setFilteredImages(images.filter(image => 
        view === 'favorites' ? image.favorited === true : view === 'trash' ? image.trashed === true : true
      ));
    } catch (err) {
      console.error('Error favoriting image:', err);
    }
  };

  return (
    <div className="gallery-page">
      <Navbar />
      <div className="main-content">
        <Sidebar setView={setView} onFilterChange={setView} /> {/* Pass onFilterChange to Sidebar */}
        <div className="gallery-content">
          {loading && <p>Loading...</p>}
          {error && <p>{error}</p>}
          {view === 'categories'
            ? categories.map((category) => (
                <ImageCard
                  key={category._id}
                  type="category"
                  data={category}
                  onClick={() => handleCardClick('category', category._id)}
                />
              ))
            : filteredImages.map((image) => (
                <ImageCard
                  key={image._id}
                  type="image"
                  data={image}
                  onClick={() => handleCardClick('image', image._id)}
                  onTrash={handleTrash}  // Pass the onTrash handler
                  onFavorite={handleFavorite}  // Pass the onFavorite handler
                />
              ))}
        </div>
      </div>
    </div>
  );
};

export default GalleryPage;
