import React from 'react';
import '../styles/ImageCard.css';

const ImageCard = ({ type, data, onClick }) => {
  if (type === 'category') {
    const { name, images = [], count } = data;

    return (
      <div className="card category-card" onClick={onClick}>
        <div className="image-grid">
          {images.length > 0 ? (
            images.slice(0, 4).map((image) => {
              // Generate the URL from imagePath stored in the database
              const imageUrl = `http://localhost:5000/uploads/${image.imagePath.split('\\').pop()}`;
              return (
                <img key={image._id} src={imageUrl} alt={name} className="thumbnail" />
              );
            })
          ) : (
            <p>No images available</p>
          )}
        </div>
        <div className="category-info">
          <h3>{name}</h3>
          <p>{count} Images</p>
        </div>
      </div>
    );
  } else if (type === 'image') {
    const { imagePath, name, date } = data;
    const imageUrl = `http://localhost:5000/uploads/${imagePath.split('\\').pop()}`;
    return (
      <div className="card image-card" onClick={onClick}>
        <img src={imageUrl} alt={name} className="thumbnail" />
        <div className="image-info">
          <h4>{name}</h4>
          <p>{date}</p>
        </div>
      </div>
    );
  }
  return null;
};

export default ImageCard;
