import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const CategoryDetail = () => {
  const { id } = useParams(); // Extract category ID from URL
  const [category, setCategory] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCategory = async () => {
      setLoading(true);
      try {
        const response = await axios.get(`http://localhost:5000/api/categories/${id}`);
        setCategory(response.data);
      } catch (err) {
        setError('Failed to load category details');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchCategory();
  }, [id]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;
  if (!category) return <p>No category found</p>;

  return (
    <div>
      <h1>{category.name}</h1>
      <p>{category.description}</p>
      <div className="image-grid">
        {category.images.map(image => (
          <img
            key={image._id}
            src={`http://localhost:5000/uploads/${image.imagePath.split('\\').pop()}`}
            alt={image.name}
            className="thumbnail"
          />
        ))}
      </div>
    </div>
  );
};

export default CategoryDetail;
