import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Navbar from '../components/NavbarGallery';
import Sidebar from '../components/SideBar';
import ImageCard from '../components/ImageCard';
import '../styles/Gallery.css';

const GalleryPage = () => {
  const [view, setView] = useState('categories');
  const [categories, setCategories] = useState([]);
  const [images, setImages] = useState([]);
  const [filteredImages, setFilteredImages] = useState([]); // For filtered images (favorites or trash)
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Fetch categories and images based on view (favorites/trash/categories)
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        if (view === 'categories') {
          const categoryResponse = await axios.get('http://localhost:5000/api/categories');
          setCategories(categoryResponse.data);
        } else {
          // Fetch images based on view ('favorites', 'trash')
          const imageResponse = await axios.get(`http://localhost:5000/api/images/`);
          setImages(imageResponse.data);
          setFilteredImages(imageResponse.data);  // Set filtered images based on the response
        }
      } catch (err) {
        setError('Failed to fetch data');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [view]);

  // Handle image click
  const handleCardClick = (type, id) => {
    if (type === 'category') {
      navigate(`/categories/${id}`);
    } else if (type === 'image') {
      navigate(`/images/${id}`);
    }
  };

  // Handle trash click - update trashed status in the database
  const handleTrash = async (id) => {
    try {
      await axios.put(`http://localhost:5000/api/images/${id}/trash`, { trashed: true });
      // After updating, fetch the data again to ensure the trashed status is reflected
      setView('trash');  // Refresh the view to show trashed images
    } catch (err) {
      console.error('Error trashing image:', err);
    }
  };

  // Handle favorite click - update favorited status in the database
  const handleFavorite = async (id, favorited) => {
    try {
      await axios.put(`http://localhost:5000/api/images/${id}/favorite`, { favorited });
      // After updating, fetch the data again to ensure the favorited status is reflected
      setView('favorites');  // Refresh the view to show favorited images
    } catch (err) {
      console.error('Error favoriting image:', err);
    }
  };

  return (
    <div className="gallery-page">
      <Navbar />
      <div className="main-content">
        <Sidebar setView={setView} />  {/* Sidebar now updates the view */}
        <div className="gallery-content">
          {loading && <p>Loading...</p>}
          {error && <p>{error}</p>}
          {view === 'categories'
            ? categories.map((category) => (
                <ImageCard
                  key={category._id}
                  type="category"
                  data={category}
                  onClick={() => handleCardClick('category', category._id)}
                />
              ))
            : filteredImages.map((image) => (
                <ImageCard
                  key={image._id}
                  type="image"
                  data={image}
                  onClick={() => handleCardClick('image', image._id)}
                  onTrash={handleTrash}  // Pass the onTrash handler
                  onFavorite={handleFavorite}  // Pass the onFavorite handler
                />
              ))}
        </div>
      </div>
    </div>
  );
};

export default GalleryPage;
