import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import SignIn from './pages/SignInPage';
import SignUp from './pages/SignUpPage';
import Gallery from './pages/GalleryPage';
import Home from './pages/HomePage';
import FavoritesPage from './pages/FavoritesPage';
import TrashPage from './pages/TrashPage';
import EditProfile from './pages/EditProfile';
import AddImage from './pages/AddImagePage';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Check authentication state (e.g., from local storage or API)
  useEffect(() => {
    const userId = localStorage.getItem('userId');
    setIsAuthenticated(!!userId);
  }, []);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route
          path="/signin"
          element={
            isAuthenticated ? (
              <Navigate to="/gallery" />
            ) : (
              <SignIn setIsAuthenticated={setIsAuthenticated} />
            )
          }
        />
        <Route path="/signup" element={<SignUp />} />
        <Route
          path="/gallery"
          element={isAuthenticated ? <Gallery /> : <Navigate to="/signin" />}
        />
        <Route path="/trash" element={isAuthenticated ? <TrashPage /> : <Navigate to="/signin" />} />
        <Route path="/favorites" element={isAuthenticated ? <FavoritesPage /> : <Navigate to="/signin" />} />
        <Route path="/edit-profile" element={isAuthenticated ? <EditProfile /> : <Navigate to="/signin" />} />
        <Route path="/add" element={isAuthenticated ? <AddImage /> : <Navigate to="/signin" />} />
      </Routes>
    </Router>
  );
};

export default App;
