import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import navigation
import Navbar from '../components/NavbarGallery';
import Sidebar from '../components/SideBar';
import ImageCard from '../components/ImageCard';
import '../styles/Gallery.css';

const GalleryPage = () => {
  const [view, setView] = useState('categories'); // 'categories' or 'images'
  const navigate = useNavigate();

  const categories = [
    {
      id: 1,
      name: 'Nature',
      images: [
        { id: 101, src: '/assets/nature1.jpg', date: '2024-11-01' },
        { id: 102, src: '/assets/nature2.jpg', date: '2024-11-02' },
        { id: 103, src: '/assets/nature3.jpg', date: '2024-11-03' },
      ],
      count: 25,
    },
    {
      id: 2,
      name: 'Travel',
      images: [
        { id: 201, src: '/assets/travel1.jpg', date: '2024-11-01' },
        { id: 202, src: '/assets/travel2.jpg', date: '2024-11-02' },
        { id: 203, src: '/assets/travel3.jpg', date: '2024-11-03' },
        { id: 204, src: '/assets/travel4.jpg', date: '2024-11-04' },
      ],
      count: 18,
    },
  ];

  const images = [
    { id: 301, src: '/assets/image1.jpg', name: 'Sunset', date: '2024-11-10' },
    { id: 302, src: '/assets/image2.jpg', name: 'Beach', date: '2024-11-11' },
    { id: 303, src: '/assets/image3.jpg', name: 'Forest', date: '2024-11-12' },
  ];

  // Navigation handler
  const handleCardClick = (type, id) => {
    if (type === 'category') {
      navigate(`/category/${id}`); // Navigate to category page
    } else if (type === 'image') {
      navigate(`/image/${id}`); // Navigate to image page
    }
  };

  return (
    <div className="gallery-page">
      <Navbar />
      <div className="main-content">
        <Sidebar setView={setView} />
        <div className="gallery-content">
          {view === 'categories'
            ? categories.map((category) => (
                <ImageCard
                  key={category.id}
                  type="category"
                  data={category}
                  onClick={() => handleCardClick('category', category.id)} // Add onClick
                />
              ))
            : images.map((image) => (
                <ImageCard
                  key={image.id}
                  type="image"
                  data={image}
                  onClick={() => handleCardClick('image', image.id)} // Add onClick
                />
              ))}
        </div>
      </div>
    </div>
  );
};

export default GalleryPage;
